import React from "react";
import { Launcher } from '@bnbsystems/react-chat-window';
import io from 'socket.io-client';

class ChatBotRobot extends React.Component {
    constructor(props) {
        super(props);
this.state = {
            messageList: [],
            socket: io("http://localhost:3000"),
            room: "user1",
        }
}

UNSAFE_componentWillMount() {
    this._sendMessage("Hey there !");
}
componentDidMount() {
    this.state.socket.connect(true);
    this.state.socket.emit('join', this.state.room);
this.state.socket.on("send-msg-response", async (msg) => {
        this.state.messageList.pop();
        await this.setState({
            messageList: [...this.state.messageList]
        })
this._sendMessage(msg);
    })
}

async _onMessageWasSent(message) {
        await this.setState({
            messageList: [...this.state.messageList, message]
        })
    }
_sendMessage(text) {
        if (text.length > 0) {
            this.setState({
                messageList: [...this.state.messageList, {
                    author: 'them',
                    type: 'text',
                    data: { text }
                },]
            })
        }
    }

render() {
return (
            <div id="chatbox" className="chatbox">
                <Launcher
                    agentProfile={{
                        teamName: 'Chatbot',
                        imageUrl: '/home/albs/Tec/Task/Bot/chatbot/public/chatbot (12).jpg'
                    }}
                    onMessageWasSent={this._onMessageWasSent.bind(this)}
                    messageList={this.state.messageList}
                    showEmoji
                />
            </div>
        );
    }
}
export default ChatBotRobot;
